$(window).scroll(function () {
    var scrollTop = $(window).scrollTop();
    if (scrollTop > $('#firstsection').offset().top) {
        $('.section.header').removeClass('off');
    }
    else {
        $('.section.header').addClass('off');
    }
});

$('.sandwichbar').click(function() {
	$('.section.header').addClass('on');
});

$('span.x').click(function() {
	$('.section.header').removeClass('on');
});

$('.card').click(function(){
    $(this).addClass('flipped').mouseleave(function(){
        $(this).removeClass('flipped');
    });
    return false;
});

$(document).ready(function() {
	$('card1').css('left', '5.5rem');
	$('card2').css('left', '15.5rem');
});

function initialize() {
    var styles =
        [
			{
				"stylers": [
					{ "invert_lightness": true }
				]
			},{
				"featureType": "road.highway",
				"elementType": "geometry.fill",
				"stylers": [
					{ "color": "#ffffff" },
					{ "lightness": -25 }
				]
			},{
				"featureType": "road.highway",
				"elementType": "geometry.stroke",
				"stylers": [
					{ "color": "#000000" }
				]
			},{
				"featureType": "road.local",
				"elementType": "geometry.fill",
				"stylers": [
					{ "color": "#ffffff" },
					{ "lightness": -25 }
				]
			},{
				"featureType": "road",
				"elementType": "labels",
				"stylers": [
					{ "invert_lightness": true },
						{ "visibility": "off" }
				]
			},{
				"featureType": "water",
				"stylers": [
					{ "color": "#535353" }
				]
			},{
				"featureType": "poi",
				"stylers": [
					{ "visibility": "off" }
				]
			},{
				"featureType": "transit",
				"stylers": [
					{ "visibility": "off" }
				]
			}
		]

    var styledMap = new google.maps.StyledMapType(styles, { name: "Styled Map" });

    var mapOptions = {
        zoom: 17,
        zoomControl: false,
        mapTypeControl: false,
        panControl: false,
        streetViewControl: false,
        scrollwheel: false,
        draggable: false,
        center: new google.maps.LatLng(38.257404, -85.757145),
        mapTypeControlOptions: {
            mapTypeIds: [google.maps.MapTypeId.ROADMAP, 'map_style']
        }
    };

    var map = new google.maps.Map(document.getElementById('map-canvas'), mapOptions);

    var image = 'content/images/humana-marker.png';
    var marker = new google.maps.Marker({
        position: new google.maps.LatLng(38.2563842,-85.7574939),
        map: map,
        icon: image
    });

    map.mapTypes.set('map_style', styledMap);
    map.setMapTypeId('map_style');
}


google.maps.event.addDomListener(window, 'load', initialize);